import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import Cookies from "js-cookie";
import jwtDecode from "jwt-decode";
import { setCurrentUser } from "../../actions/AuthActions";
import useSession from "react-session-hook";

const NavBar = () => {
  const dispatch = useDispatch();
  const authDetail = useSelector((state) => state.auth);
  useEffect(() => {
    if (Cookies.get("token")) {
      session.setSession({ token: Cookies.get("token") })
      dispatch(setCurrentUser(Cookies, jwtDecode));
    }
  }, []);
  const session = useSession();
  const handleLogout = () => session.removeSession();
  return (
    <div className="auth-btns col-md-7">
      {session.isAuthenticated ? (
        <div className="float-right mt-3">
          {authDetail.current_user? authDetail.current_user.sub:''}
          {session.profile.name}
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <>
          <NavLink to="/signup">
            <button className="btn sign-up">Sign Up</button>
          </NavLink>
          <NavLink to="/signin">
            <button className="btn sign-in">Sign In</button>
          </NavLink>
        </>
      )}
    </div>
  );
};

export default NavBar;
