import React from 'react';
import { useSelector } from "react-redux";
import _ from "lodash";
import './App.css';
import { Switch, Route , NavLink, Redirect} from "react-router-dom"
import UserList from './containers/UserList';
import User from './containers/User';
import Cookies from 'js-cookie';
import jwtDecode from 'jwt-decode';

function App() {

  const authDetail = useSelector((state) => state.auth);
  useEffect(() => {
    if (Cookies.get('token')) {
      setCurrentUser(Cookies, jwtDecode);
    }
  }, []);
  return (
    <div className="App">
      <nav>
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-5">
            <div className="logo"><a href="/">Best To Do List</a></div>
          </div>
          <div className="auth-btns col-md-7">
            {authDetail ? (
              <div className="float-right mt-3">{authDetail.user}</div>
            ) : (
              <>
                <NavLink to="/signup">
                  <button className="btn sign-up">Sign Up</button>
                </NavLink>
                <NavLink to="/signin">
                  <button className="btn sign-in">Sign In</button>
                </NavLink>
              </>
            )}
          </div>
        </div>
      </div>
        <NavLink to={"/"}>Search</NavLink>
      </nav>
      <Switch>
        <Route path={"/"} exact component={UserList} />
        <Route path={"/user/:user"} exact component={User} />
        <Redirect to={"/"} />
      </Switch>
    </div>
  );
}

export default App;
