import React, { useState } from 'react';
import { useSelector,  useDispatch } from "react-redux";
import classnames from 'classnames';
import { NavLink } from 'react-router-dom';
import { signIn } from "../../actions/AuthActions"
import { Redirect } from "react-router-dom";
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBBtn, MDBIcon, MDBModalFooter } from 'mdbreact';


const SignIn = () => {
  const dispatch = useDispatch();
  const state = useSelector((state) => state.auth);
  const [username, setusername] = useState('');
  const [password, setpassword] = useState('');
  const [nameErr, setnameErr] = useState('');
  const [passwordErr, setpasswordErr] = useState('');

  const handleChange = (e, name) => {
    const user = {};
    user[name] = e.target.value;
    // validations
    switch (name) {
      case 'username':
        setusername(user.username);
        user.username.length < 3 ? setnameErr('Name must be at least 3 characters!') : setnameErr('');
        break;
      case 'password':
        setpassword(user.password);
        user.password.length < 8 ? setpasswordErr('Password must be at least 8 characters!') : setpasswordErr('');
        break;
      default:
        break;
    }
  }

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (username && password && !nameErr && !passwordErr) {
      dispatch(signIn({ username, password }));
    }
  }
  const authDetail = useSelector((state) => state.auth);
  if(authDetail.isAuthenticated)
    return <Redirect to={"/"} />
  else
  return (
    <MDBContainer>
    <MDBRow>
      <MDBCol md="6">
        <MDBCard>
          <MDBCardBody className="mx-4">
            <div className="text-center">
              <h3 className="dark-grey-text mb-5">
                <strong>Sign in</strong>
              </h3>
            </div>
            <MDBInput
              label="Your email"
              group
              type="email"
              validate
              error="wrong"
              success="right"
            />
            <MDBInput
              label="Your password"
              group
              type="password"
              validate
              containerClass="mb-0"
            />
            <p className="font-small blue-text d-flex justify-content-end pb-3">
              Forgot
              <a href="#!" className="blue-text ml-1">

                Password?
              </a>
            </p>
            <div className="text-center mb-3">
              <MDBBtn
                type="button"
                gradient="blue"
                rounded
                className="btn-block z-depth-1a"
              >
                Sign in
              </MDBBtn>
            </div>
            <p className="font-small dark-grey-text text-right d-flex justify-content-center mb-3 pt-2">

              or Sign in with:
            </p>
            <div className="row my-3 d-flex justify-content-center">
              <MDBBtn
                type="button"
                color="white"
                rounded
                className="mr-md-3 z-depth-1a"
              >
                <MDBIcon fab icon="facebook-f" className="blue-text text-center" />
              </MDBBtn>
              <MDBBtn
                type="button"
                color="white"
                rounded
                className="mr-md-3 z-depth-1a"
              >
                <MDBIcon fab icon="twitter" className="blue-text" />
              </MDBBtn>
              <MDBBtn
                type="button"
                color="white"
                rounded
                className="z-depth-1a"
              >
                <MDBIcon fab icon="google-plus-g" className="blue-text" />
              </MDBBtn>
            </div>
          </MDBCardBody>
          <MDBModalFooter className="mx-5 pt-3 mb-1">
            <p className="font-small grey-text d-flex justify-content-end">
              Not a member?
              <a href="#!" className="blue-text ml-1">

                Sign Up
              </a>
            </p>
          </MDBModalFooter>
        </MDBCard>
      </MDBCol>
    </MDBRow>
  </MDBContainer>
    // <div>
    //     <h2 className="text-center">Sign In</h2>
    //     {state.signInErr && <div className="alert alert-danger text-center">
    //       <span className="text-danger text-capitalize">{state.signInErr}</span>
    //     </div>}
    //     <form className="mt-4" onSubmit={handleSignIn}>
    //       <div className="form-group">
    //         <label htmlFor="username">Enter username </label>
    //         <input
    //           type="text"
    //           name="username"
    //           className={classnames(
    //             "form-control",
    //             { 'is-invalid': nameErr, 'is-valid': !nameErr && username.length }
    //           )}
    //           id="username"
    //           placeholder="Full username"
    //           onChange={(e) => handleChange(e, 'username')}
    //         />
    //         {nameErr && <small className="text-danger">{nameErr}</small>}
    //       </div>
    //       <div className="form-group">
    //         <label htmlFor="password">Enter Password</label>
    //         <input
    //           type="password"
    //           name="password"
    //           className={classnames(
    //             "form-control",
    //             { 'is-invalid': passwordErr, 'is-valid': !passwordErr && password.length }
    //           )}
    //           id="password"
    //           placeholder="Password"
    //           onChange={(e) => handleChange(e, 'password')}
    //         />
    //         {passwordErr && <small className="text-danger">{passwordErr}</small>}
    //       </div>
    //       <input type="submit" className="btn btn-primary" value="Sign In" />
    //     </form>
    //     <p className="float-left">
    //       Don't yet have an account? <NavLink to="/signup">Sign Up</NavLink>
    //     </p>
    //   </div>
  )
}

export default SignIn
