import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";
import "./App.css";
import { Switch, Route, NavLink, Redirect } from "react-router-dom";
import UserList from "./containers/UserList";
import User from "./containers/User";
import NavBar from "./containers/layouts/NavBar";
import {signUp , signIn} from "./actions/AuthActions"

function App() {
  return (
    <div className="App">
      <nav>
        <NavBar />
      </nav>
      <Switch>
      <Route path="/signup" render={({ history }) => (<SignUp signUp={async (user) => {
            const res = await signUp(user);
            if (res) {
              Cookies.set('token', res.data.token);
              history.push('/todos');
            }
          }} />)
        } />
        <Route path="/signin" render={({ history }) => (<SignIn signIn={async (user) => {
          const res = await signIn(user);
          if (res) {
            Cookies.set('token', res.data.token);
            history.push('/todos');
          }
        }} />)} />
        <Route path={"/"} exact component={UserList} />
        <Route path={"/user/:user"} exact component={User} />
        <Redirect to={"/"} />
      </Switch>
    </div>
  );
}

export default App;
