import apiCall from '../apiCall';
import Cookies from "js-cookie";
import jwtDecode from "jwt-decode";
import History from '../History'

export const signUp = (user) => async (dispatch) => {  
  try {
    dispatch({ type: 'SIGNUP_USER_LOADING' });
    const res = await apiCall('/auth/signup', 'post', user);
    dispatch({ type: 'SIGNUP_USER_SUCCESS', payload: res.data });
    History.push(`${app}/signin`)
    return res;
  } catch (err) {
    console.log(err.response)
    return dispatch({ type: 'SIGNUP_USER_FAILURE', payload: err.response.data });
  }
};

export const setCurrentUser = (Cookies, jwtDecode)=> async  (dispatch) => {
  try {
    dispatch({ type: 'SET_CURRENT_USER_LOADING' });
    const payload = jwtDecode(Cookies.get('token'));
    dispatch({ type: 'SET_CURRENT_USER_SUCCESS', payload });
  } catch (err) {
    dispatch({ type: 'SET_CURRENT_USER_FAILURE', payload: err.response.data })
  }
}

export const signIn = (user) => async (dispatch) => {
  try {
    dispatch({ type: 'SIGNIN_USER_LOADING' });
    const res = await apiCall('/auth/signin', 'post', user);
    dispatch({ type: 'SIGNIN_USER_SUCCESS', payload: res.data });
    Cookies.set('token', res.data.accessToken);
    dispatch(setCurrentUser(Cookies,jwtDecode))
    return res;
  } catch (err) {
    return dispatch({ type: 'SIGNIN_USER_FAILURE', payload: err.response.data })
  }
};
export const signUpDone = () => async (dispatch ) =>{
  try {
    dispatch({ type: 'SIGNUPDONE_USER_SUCCESS'});
  } catch (err) {
    return dispatch({ type: 'SIGNUPDONE_USER_FAILURE', payload: err.response.data })
  }
}
export const logoutUser = ()=> async  (dispatch) => {
  try {
    dispatch({ type: 'LOGOUT_USER_LOADING' });
    dispatch({ type: 'LOGOUT_USER_SUCCESS'});
  } catch (err) {
    dispatch({ type: 'SET_CURRENT_USER_FAILURE', payload: err.response.data })
  }
}
