const initialState = {
  user: null,
  current_user: null,
  isAuthenticated: false,
  signUpErr: "",
  signInErr: "",
  signUpSuccess: "",
  loading: false
};

const auth = (state = initialState, action) => {
  switch (action.type) {
    case "AUTH_LOADING":
          return {
              ...state,
              loading: true
          };
    case "SIGNUP_USER_SUCCESS":
      return { ...state, signUpSuccess: action.payload.message,signUpErr: "" };
    case "SIGNUP_USER_FAILURE":
      return { ...state, signUpErr: action.payload.message, signUpSuccess: "", };
    case "SET_CURRENT_USER_SUCCESS":
      return { ...state, current_user: action.payload };

    // Sign in cases
    case "SIGNIN_USER_SUCCESS":
      return { ...state, user: action.payload, isAuthenticated: true ,signInErr: "",};
    case "SIGNIN_USER_FAILURE":
      return { ...state, signInErr: action.payload.error };
    case "LOGOUT_USER_SUCCESS":
      return {
        user: null,
        current_user: null,
        isAuthenticated: false,
        signUpErr: "",
        signInErr: "",
        signUpSuccess: "",
      };
    case "SIGNUPDONE_USER_SUCCESS":
      return { ...state, signUpSuccess: "" };
    default:
      return state;
  }
};

export default auth;
