import React, { useState } from 'react';
import { useSelector,  useDispatch } from "react-redux";
import classnames from 'classnames';
import { NavLink } from 'react-router-dom';
import { signIn } from "../../actions/AuthActions"
import { Redirect } from "react-router-dom";
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));
const SignIn = () => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const state = useSelector((state) => state.auth);
  const [username, setusername] = useState('');
  const [password, setpassword] = useState('');
  const [nameErr, setnameErr] = useState('');
  const [passwordErr, setpasswordErr] = useState('');

  const handleChange = (e, name) => {
    const user = {};
    user[name] = e.target.value;
    // validations
    switch (name) {
      case 'username':
        setusername(user.username);
        user.username.length < 3 ? setnameErr('Name must be at least 3 characters!') : setnameErr('');
        break;
      case 'password':
        setpassword(user.password);
        user.password.length < 8 ? setpasswordErr('Password must be at least 8 characters!') : setpasswordErr('');
        break;
      default:
        break;
    }
  }

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (username && password && !nameErr && !passwordErr) {
      dispatch(signIn({ username, password }));
    }
  }
  const authDetail = useSelector((state) => state.auth);
  if(authDetail.isAuthenticated)
    return <Redirect to={"/"} />
  else
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="#" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
    // <div>
    //     <h2 className="text-center">Sign In</h2>
    //     {state.signInErr && <div className="alert alert-danger text-center">
    //       <span className="text-danger text-capitalize">{state.signInErr}</span>
    //     </div>}
    //     <form className="mt-4" onSubmit={handleSignIn}>
    //       <div className="form-group">
    //         <label htmlFor="username">Enter username </label>
    //         <input
    //           type="text"
    //           name="username"
    //           className={classnames(
    //             "form-control",
    //             { 'is-invalid': nameErr, 'is-valid': !nameErr && username.length }
    //           )}
    //           id="username"
    //           placeholder="Full username"
    //           onChange={(e) => handleChange(e, 'username')}
    //         />
    //         {nameErr && <small className="text-danger">{nameErr}</small>}
    //       </div>
    //       <div className="form-group">
    //         <label htmlFor="password">Enter Password</label>
    //         <input
    //           type="password"
    //           name="password"
    //           className={classnames(
    //             "form-control",
    //             { 'is-invalid': passwordErr, 'is-valid': !passwordErr && password.length }
    //           )}
    //           id="password"
    //           placeholder="Password"
    //           onChange={(e) => handleChange(e, 'password')}
    //         />
    //         {passwordErr && <small className="text-danger">{passwordErr}</small>}
    //       </div>
    //       <input type="submit" className="btn btn-primary" value="Sign In" />
    //     </form>
    //     <p className="float-left">
    //       Don't yet have an account? <NavLink to="/signup">Sign Up</NavLink>
    //     </p>
    //   </div>
  )
}

export default SignIn
